BlinkMacSystemFont,
San Francisco,
Segoe UI,
メイリオ,
Meiryo,
Segoe UI,
sans-serif;
$fontWeightRegular: 400;
$fontWeightMedium: 500;
$fontWeightBold: 700;
$fontSizeH1: 6.625rem;
$fontSizeH1sub: 5rem;
$fontSizeH2: 2.375rem;
$fontSizeH3: 1.625rem;
$fontSizeBodyL: 1.25rem;
$fontSizeBodyM: 1.125rem;
$fontSizeBodyS: 1rem;
$fontSizeBodyXS: 0.8rem;
$spaceLetters:0.45px;
$lineHeightTitle: 1.1;
$lineHeightBody: 1.5;
$maxWidthS: 480px;
$maxWidthM: 640px;
$maxWidthL: 1096px;
$spaceOuter: 64px;
$spaceXS: 4px;
$spaceS: 8px;
$spaceM: 16px;
$spaceL: 24px;
$spaceXL: 32px;
$space2XL: 48px;
$space3XL: 64px;
$space4XL: 96px;
$space5XL: 128px;

*,
html {
    -webkit-user-drag: none;
    -moz-user-drag: none;
    -ms-user-drag: none;
    -webkit-user-modify: none;
    -moz-user-modify: none;
    -ms-user-modify: none;
    user-modify: none;
    -webkit-tap-highlight-color: transparent;
    tap-highlight-color: transparent;
    -webkit-font-smoothing: antialiased;
    font-smoothing: antialiased;
}

body {
    background: transparent;
    width: 100%;
    box-sizing: border-box;
    font-family: $fontStack;
    font-weight: $fontWeightRegular;
    font-synthesis: none;
    color: black;
    border: 0;
    margin: 0;
    overflow-x: hidden;
    position: relative;
    background-color: black;

    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    span,
    p,
    div,
    a {
        &::selection {
            background: black;
            color: white;
        }
    }
}

.container-fluid {
    width: 100%;
    max-width: 1700px;
}

.header {
    // position: fixed;
    z-index: 1024;
    width: 100%;
    display: flex;
    top: 1rem;

    .container-fluid {
        background: linear-gradient(45deg, #BB231B 0%, #2216af 100%);
        border-radius: 10px;
        position: relative;
        overflow: hidden;
        box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
        animation: leftToRight 0.75s forwards;

        &::before {
            content: '';
            display: block;
            position: absolute;
            top: 0;
            left: -500%;
            width: 500%;
            height: 100%;
            border-radius: 10px;
        }

    }


    &__logo {
        display: flex;
        position: relative;
        padding: $spaceS;
        z-index: 16;
        transition: 0.5s $bezierFastoutSlowin;
        cursor: pointer;
        color: white;
        line-height: 2.5rem;
        font-size: $fontSizeBodyL;

        &:hover {
            transform: scale(1.1);
        }

        &:active {
            transform: scale(1);
        }

        svg {
            width: 2.5rem;
            height: 2.5rem;
            padding: 0.25rem 0;
            margin-right: 1rem;

            path,
            rect {
                fill: #FA709A;
            }
        }
    }

    .nav-toggle {
        position: fixed;
        top: 1rem;
        right: 2.5rem;
        z-index: 1024;
        display: none;
        background: none;
        border: 0;
        outline: 0;
        height: 48px;
        align-items: center;

        svg {
            color: $rgbWhite08;
            fill: $rgbWhite08;
            transition: $bezierFastoutSlowin 0.5s;
            top: 50%;
            left: 50%;
            position: absolute;
            width: 28px;
            height: 28px;

            path {
                fill: white;
                transition: $bezierFastoutSlowin 0.5s;
            }
        }

        &__inner {
            position: relative;
        }

        &__icon--menu {
            opacity: 1;
            transform: translate(-50%, -50%);
        }

        &__icon--close {
            opacity: 0;
            transform: translate(-50%, -50%) rotate(45deg);
        }
    }


    .button-icon-only {
        align-items: center;
        justify-content: center;
        padding: 0;
    }

    &__nav {
        flex-grow: 10;
        display: flex;
        align-items: center;
        justify-content: flex-end;
    }

    &__nav-list {
        display: flex;
    }

    &__nav-link {
        padding: $spaceM;
        color: white;
        font-weight: $fontWeightMedium;
        font-size: initial;
        text-decoration: none;
        position: relative;
        transition: color $durationS ease .1s;
        line-height: 1;
        padding: $spaceS;
        margin: $spaceS;
        border-radius: $spaceXS;
        letter-spacing: $spaceLetters;
        transition: $bezierFastoutSlowin 0.36s;

        &:active,
        &:hover {
            text-decoration: none;
            background-color: white;
            color: black;
        }

        &.active {
            background-color: white;
            color: black;
        }
    }

    &__nav-icons {
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;
        z-index: 16;

        svg path {
            fill: white;
        }
    }

    &__nav-icon-link {
        cursor: pointer;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: $spaceS;
        width: $space2XL;
        height: $space2XL;
        color: white;
        transition: $durationM $bezierFastoutSlowin;

        &:hover {
            svg {
                transform: scale(1.2);
            }
        }

        &:active {
            svg {
                transform: scale(1);
            }
        }

        svg {
            padding: 1px;
            transition: $durationM ease;

            path {
                fill: white;
                transition: fill $durationM ease;
            }
        }
    }

}

.main {
    // margin-top: calc(1rem + 72px + 1rem);
    margin-top: calc(1rem + 1rem);
}

@keyframes introBackground {
    from {
        background-position: top right;
    }

    to {
        background-position: bottom left;
    }
}

@keyframes leftToRight {
    from {
        left: -150%;
    }

    to {
        left: 0;
    }
}

@keyframes rightToLeft {
    from {
        right: -150%;
    }

    to {
        right: 0;
    }
}

@keyframes bottomToTop {
    from {
        bottom: -25%;
        opacity: 0;
    }

    to {
        bottom: 0;
        opacity: 1;
    }
}

@keyframes scaleAndOpacity {
    from {
        opacity: 0;
        transform: scale(0.3);
    }

    to {
        opacity: 1;
        transform: scale(1);
    }
}

.intro {
    position: relative;
    transition: 0.7s;
    border-radius: 15px;
    display: flex;
    justify-content: center;

    .container-fluid {
        position: relative;
        height: calc(100vh - 72px - 3rem);
        background: linear-gradient(45deg, #BB231B 0%, #2216af 100%);
        border-radius: 15px;
        margin: 0 1rem;
        // left: -50%;
        // width: 150%;
        right: -150%;
        animation: rightToLeft 0.75s forwards;
        animation-delay: 1s;
        // padding-left: 50%;

        &::before {
            content: '';
            width: 100%;
            height: 100%;
            left: 0;
            top: 0;
            content: '';
            opacity: 0.6;
            z-index: 0;
            position: absolute;
            background: url('../images/ship.jpeg');
            background-size: cover;
            background-position: bottom left;
            border-radius: 15px;
            // animation: introBackground 2s forwards;
        }
    }

    &__heading {
        font-weight: 900;
        color: white;
        font-size: $fontSizeH1sub;
        position: relative;
        z-index: 2;
        font-size: $fontSizeH1;
        width: 100%;
        margin-left: 2rem;
        opacity: 0;
        animation: bottomToTop 0.5s forwards 2s;

        &-container {
            width: 100%;
            display: flex;
            height: 100%;
            align-items: center;
        }

        &--company-name {
            font-size: $fontSizeH1sub;
            display: block;
        }
    }


    &__btn {
        // background: #2216af;
        animation: scaleAndOpacity 0.5s forwards 2.5s;
        transform: scale(0.3);
        opacity: 0;
        border-radius: 8px;
        color: white;
        display: inline-block;
        position: relative;
        overflow: hidden;
        z-index: 3;
        padding: 1rem 2rem;
        letter-spacing: 0.45px;
        border: none;
        transition: 0.3s;
        box-shadow: rgba(0, 0, 0, 0) 0px 0px 0px 0px, rgba(0, 0, 0, 0) 0px 0px 0px 0px, rgba(0, 0, 0, 0.25) 0px 25px 50px -12px;

        &-text {
            position: relative;
            z-index: 2;
            font-weight: 500;
        }

        &::before {
            content: '';
            bottom: -50%;
            height: 200%;
            width: 100%;
            position: absolute;
            left: 0;
            background: linear-gradient(45deg, #BB231B 0%, #2216af 100%);
            transition: 0.3s;
            z-index: 1;
        }

        &:hover {
            color: white;
            background: linear-gradient(45deg, #BB231B 0%, #2216af 100%);
            border: none;
            outline: none;
            transform: scale(1.1);

            &::before {
                bottom: 0;
            }
        }
    }
}


.big-title {
    font-size: 10vw;
    font-weight: 500;
    margin: 1rem auto;
    display: inline-block;
    background: linear-gradient(45deg, $darkRed 0%, $blue 100%);
    background: -webkit-linear-gradient(45deg, $darkRed 0%, $blue 100%);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;

    &::selection {
        color: white;
        background: black;
        background-clip: initial;
    }
}

.about {
    color: white;


    &__container-helper {
        min-height: 50vh;
    }

    &__text-container {
        background: linear-gradient(45deg, rgb(17, 17, 17) 0%, $darkRed 100%);
        border-radius: 15px;
        min-height: 20vh;
        padding: 1.5rem;
    }

    &__carousel {


        .owl-stage-outer {
            border-radius: 15px;
        }

        .owl-dots {
            position: absolute;
            bottom: 2rem;
            left: 50%;
            transform: translateX(-50%);

            .owl-dot {
                border-radius: 10px;
                width: 3rem;
                height: 1rem;
                background: black;
                margin: 0 0.25rem;

                &:focus {
                    outline: none;
                    border: none;
                }

                &.active {
                    background: linear-gradient(45deg, rgb(17, 17, 17) 0%, $darkRed 100%);
                }
            }
        }
    }

    &__img {
        border-radius: 15px;
        width: 100%;
        object-fit: cover;
        position: relative;
        height: 100%;
        min-height: 70vh;
        max-height: 100vh;
        background: linear-gradient(45deg, $darkRed 30%, $darkGrey 100%);

        &::before {
            content: '';
            width: 100%;
            height: 100%;
            left: 0;
            top: 0;
            content: '';
            opacity: 0.6;
            filter: brightness(110%);
            z-index: 0;
            position: absolute;
            background: url('../images/logistics-people.jpg');
            background-size: cover;
            background-position: center;
            border-radius: 15px;
        }

        &-2::before {
            background: url('../images/ship.jpeg');
            background-size: cover;
            background-position: center;
        }
    }
}

.offer {

    margin-top: calc(1.5rem + 1.5rem);
    color: white;

    &__text-container {
        background: $darkGrey;
        background: linear-gradient(45deg, $darkGrey -40%, #2216af 200%);
        border-radius: 15px;
        min-height: 30vh;
        padding: 1.5rem;
        display: flex;
        flex-direction: column;
        justify-content: flex-end;
        position: relative;
        overflow: hidden;
    }

    &__heading-svg {
        width: 30rem;
        height: 30rem;
        position: absolute;
        top: -4rem;
        right: 1rem;
    }

    &__carousel {
        &-item {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
        }

        &-link {
            color: white;
            border: 4px solid transparent;
            border-radius: 10px;
            transition: 0.3s;
            padding: 1.5rem;
            position: relative;
            background: #000;
            background-clip: padding-box;

            &::before {
                content: '';
                position: absolute;
                top: 0;
                right: 0;
                bottom: 0;
                left: 0;
                z-index: -1;
                margin: 1px;
                /* !importanté */
                border-radius: inherit;
                /* !importanté */
                background: black;
                transition: 0.3s;
                background: linear-gradient(45deg, $darkGrey -40%, #2216af 200%);
            }

            &:hover {
                color: white;
                outline: none;
                text-decoration: none;

                &::before {
                    margin: -4px;
                }

                .offer__carousel-btn {
                    &::before {
                        opacity: 1;
                        transform: scale(1);
                    }
                }

            }
        }

        &-svg {
            display: block;
            height: 3rem;
            width: 100%;

            path {
                stroke: white;
            }
        }

        &-item-title {
            text-align: center;
            color: white;
            outline: none;
            text-decoration: none;
        }

        &-btn {
            width: 100%;
            border: none;
            outline: none;
            color: white;
            background: $darkGrey;
            transition: 0.3s;
            position: relative;
            color: white;
            border-radius: 10px;
            overflow: hidden;
            transition: 0.3s;

            &-text {
                color: white;
                position: relative;
                z-index: 2;
            }

            &::before {
                content: '';
                bottom: 0;
                height: 100%;
                width: 100%;
                transform: scale(0);
                opacity: 0;
                position: absolute;
                left: 0;
                border-radius: 10px;
                background: linear-gradient(45deg, $darkGrey -40%, #2216af 200%);
                transition: 0.3s;
                z-index: 1;
            }

        }
    }
}

.jobs {
    margin-top: 25vh;

    .big-title {
        background: linear-gradient(45deg, $darkRed 0%, $blue 60%);
        background: -webkit-linear-gradient(45deg, $darkRed 0%, $blue 60%);
        background-clip: text;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
    }

    &__img {
        border-radius: 15px;
        width: 100%;
        object-fit: cover;
        position: relative;
        height: 100%;
        min-height: 100vh;
        background: linear-gradient(45deg, $darkGrey 0%, $blue 50%);

        &::before {
            content: '';
            width: 100%;
            height: 100%;
            left: 0;
            top: 0;
            content: '';
            opacity: 0.6;
            filter: brightness(20%);
            z-index: 0;
            position: absolute;
            background: url('../images/shake-hand.jpeg');
            background-size: cover;
            background-position: center;
            border-radius: 15px;
        }
    }

    &__subheading {
        position: absolute;
        top: 0;
        color: white;
        font-weight: 900;
        letter-spacing: 0.45px;
        font-size: $fontSizeH1sub;
        line-height: 2;
        width: 100%;
        text-align: center;
    }

    &__job-container {
        display: grid;
        grid-template-columns: 1fr 1fr;
        grid-auto-rows: fit-content(1em);
        column-gap: 1.5rem;
        row-gap: 1.5rem;
    }

    &__item {
        background: $darkGrey;
        border-radius: 15px;
        display: inline-block;
        padding: 1.25rem;
        color: white;
        border: 4px solid $darkGrey;
        transition: 0.3s;
        position: relative;

        &:hover {
            transform: scale(1.05);
        }
    }

    &__place {
        display: flex;
        align-items: center;

        &-text {
            font-weight: 500;
        }
    }

    &__svg {
        display: block;
        height: 2rem;
        margin-right: 1rem;

        path {
            stroke: white;
        }
    }

    &__item-title {
        color: white;
        outline: none;
        text-decoration: none;
        letter-spacing: 0.45px;
        font-weight: 700;
        font-size: $fontSizeH3;
        margin: 0;
    }

    &__btn {
        width: 100%;
        border: none;
        outline: none;
        color: white;
        background: $darkGrey;
        transition: 0.3s;
        position: relative;
        color: white;
        border-radius: 10px;
        overflow: hidden;
        transition: 0.3s;
        background: white;

        &-text {
            color: black;
            position: relative;
            z-index: 2;
            transition: 0.3s;
        }

        &::before {
            content: '';
            bottom: 0;
            height: 100%;
            width: 100%;
            transform: scale(0);
            opacity: 0;
            position: absolute;
            left: 0;
            border-radius: 10px;
            background: linear-gradient(45deg, $darkGrey -40%, #2216af 200%);
            transition: 0.3s;
            z-index: 1;
        }

        &:hover {
            color: white;
            outline: none;
            text-decoration: none;
            background: transparent;

            .jobs__btn-text {
                color: white;
            }

            &::before {
                opacity: 1;
                transform: scale(1.1);

            }
        }

        &:focus {
            outline: none;
            box-shadow: none;
        }

    }
}
