    h2,
    h3,
    h4,
    h5,
    h6,
    span,
    p,
    div,
    a {
        &::selection {
            background: black;
            color: white;
        }
    }
}

.header {
    position: fixed;
    z-index: 1024;
    width: 100%;
    display: flex;
    top: 1rem;
    transition: 0.5s;

    .container {
        position: relative;

        background: transparent;
        border-radius: .0010px;
        overflow: hidden;
        // animation: leftToRight 0.75s forwards;
        transform: translateY(-100px);
        opacity: 0;
        animation: topToBottom 0.75s forwards 0.75s;
        transition: 0.3s;


        &::before {
            content: '';
            display: block;
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            border-radius: .0010px;
            background: linear-gradient(45deg, $libertyPurple 0%, $unitedNationsBlue 100%);
            box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
            transition: 0.5s;
            opacity: 0;
        }

        &.scrolled {
            &::before {
                // top: 0;
                opacity: 1;
            }
        }



    }


    &__logo {
        display: flex;
        position: relative;
        padding: $spaceS;
        z-index: 16;
        transition: 0.5s $bezierFastoutSlowin;
        cursor: pointer;
        color: white;
        line-height: 2.5rem;
        font-size: $fontSizeBodyL;

        &:hover {
            transform: scale(1.1);
        }

        &:active {
            transform: scale(1);
        }

        svg {
            width: 2.5rem;
            height: 2.5rem;
            padding: 0.25rem 0;
            margin-right: 1rem;

            path,
            rect {
                fill: white;
            }
        }
    }

    .nav-toggle {
        // position: fixed;
        top: 1rem;
        right: 2.5rem;
        margin-right: 1rem;
        z-index: 1024;
        display: none;
        background: none;
        border: 0;
        outline: 0;
        height: 48px;
        align-items: center;

        svg {
            color: white;
            fill: white;
            transition: $bezierFastoutSlowin 0.5s;
            top: 50%;
            left: 50%;
            position: absolute;
            width: 28px;
            height: 28px;

            path {
                fill: white;
                transition: $bezierFastoutSlowin 0.5s;
            }
        }

        &__inner {
            position: relative;
        }

        &__icon--menu {
            opacity: 1;
            transform: translate(-50%, -50%);
        }

        &__icon--close {
            opacity: 0;
            transform: translate(-50%, -50%) rotate(45deg);
        }
    }


    .button-icon-only {
        align-items: center;
        justify-content: center;
        padding: 0;
    }

    &__nav {
        flex-grow: 10;
        display: flex;
        align-items: center;
        justify-content: flex-end;
    }

    &__nav-list {
        display: flex;
    }

    &__nav-link {
        padding: $spaceM;
        color: white;
        font-weight: $fontWeightMedium;
        font-size: initial;
        text-decoration: none;
        position: relative;
        transition: color $durationS ease .1s;
        line-height: 1;
        padding: $spaceS;
        margin: $spaceS;
        border-radius: .00$spaceXS;
        letter-spacing: $spaceLetters;
        transition: $bezierFastoutSlowin 0.36s;

        &:active,
        &:hover {
            text-decoration: none;
            background-color: white;
            color: black;
        }

        &.active {
            background-color: white;
            color: black;
        }
    }

    &__nav-icons {
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;
        z-index: 16;

        svg path {
            fill: white;
        }
    }

    &__nav-icon-link {
        cursor: pointer;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: $spaceS;
        width: $space2XL;
        height: $space2XL;
        color: white;
        transition: $durationM $bezierFastoutSlowin;

        &:hover {
            svg {
                transform: scale(1.2);
            }
        }

        &:active {
            svg {
                transform: scale(1);
            }
        }

        svg {
            padding: 1px;
            transition: $durationM ease;

            path {
                fill: white;
                transition: fill $durationM ease;
            }
        }
    }

}

@keyframes introBackground {
    from {
        background-position: top right;
    }

    to {
        background-position: bottom left;
    }
}

@keyframes leftToRight {
    from {
        left: -150%;
    }

    to {
        left: 0;
    }
}

@keyframes rightToLeft {
    from {
        right: -150%;
    }

    to {
        right: 0;
    }
}

@keyframes bottomToTop {
    from {
        bottom: -25%;
        opacity: 0;
    }

    to {
        bottom: 0;
        opacity: 1;
    }
}

@keyframes topToBottom {
    from {
        transform: translateY(-100px);
        opacity: 0;
    }

    to {
        transform: translateY(0);
        opacity: 1;
    }
}

@keyframes scaleAndOpacity {
    from {
        opacity: 0;
        transform: scale(0.3);
    }

    to {
        opacity: 1;
        transform: scale(1);
    }
}

.intro {
    position: relative;
    transition: 0.7s;
    width: 100%;
    position: relative;
    height: 100vh;

    &__carousel {
        height: 100%;
        position: absolute;
        top: 0;
        left: 0;

        &-item {
            position: relative;
            height: 100%;
        }

        .owl-stage-outer {
            // border-radius:  .0015px;
            height: 100%;

            .owl-stage {
                height: 100%;
            }
        }

        .owl-item {
            height: 100%;
        }

        .owl-nav {
            position: absolute;
            top: 50%;
            width: 100%;
            transform: translateY(-50%);
            display: flex;
            justify-content: space-between;

            button.owl-next span,
            button.owl-prev span,
            button.owl-dot span {
                background: transparent;
                width: 4rem;
                height: 7rem;
                margin: 0 1.5rem;
                background-image: url('../images/chevron-left.svg');
                background-size: contain;
                background-repeat: no-repeat;
                background-position: center;
                transition: 0.3s;
                color: transparent;
                display: inline-block;


                &:hover {
                    transform: translate(1.5vmin, 1.5vmin);
                }

                &:active {
                    transform: translate(1.5vmin, 1.5vmin) scale(0.9);
                }
            }

            button:focus {
                outline: none;
                box-shadow: none;
                border: none;
            }

            .owl-nav button.owl-next,
            .owl-nav button.owl-prev,
            button.owl-dot {

                &:focus,
                &:active {
                    outline: none;
                    box-shadow: none;
                    border: none;
                }
            }

            button.owl-next span {
                transform: rotate(180deg);

                &:hover {
                    transform: rotate(180deg) translate(1.5vmin, 1.5vmin);
                }

                &:active {

                    transform: rotate(180deg) translate(1.5vmin, 1.5vmin) scale(0.9);
                }
            }
        }
    }

    &__img {
        // border-radius:  .0015px;
        width: 100%;
        object-fit: cover;
        position: absolute;
        height: 100vh;
        background: linear-gradient(45deg, $unitedNationsBlue 30%, $darkGrey 100%);

        &::before {
            content: '';
            width: 100%;
            height: 100%;
            left: 0;
            top: 0;
            content: '';
            opacity: 0.6;
            filter: brightness(110%);
            z-index: 0;
            position: absolute;
            background: url('../images/logistics-people.jpg');
            background-size: cover;
            background-position: center;
            // border-radius:  .0015px;
        }

        &-2::before {
            background: url('../images/ship.jpeg');
            background-size: cover;
            background-position: center;
        }
    }


    .container {
        height: 100%;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        z-index: 3;
    }


    &__heading {
        font-weight: 900;
        color: white;
        position: relative;
        z-index: 2;
        font-size: $fontSizeH1;
        width: 100%;
        opacity: 0;
        animation: bottomToTop 0.5s forwards 2s;

        &-container {
            width: 100%;
            display: flex;
            height: 100%;
            align-items: center;
        }
    }

    &__subheading {
        color: white;
        font-size: $fontSizeBodyL;
        max-width: 800px;
        display: block;
        opacity: 0;
        animation: bottomToTop 0.5s forwards 2.5s;
    }


    &__btn {
        // background: $unitedNationsBlue;
        animation: scaleAndOpacity 0.5s forwards 3s;
        transform: scale(0.3);
        opacity: 0;
        border-radius: .008px;
        color: white;
        display: inline-block;
        position: relative;
        overflow: hidden;
        z-index: 3;
        padding: 1rem 2rem;
        letter-spacing: 0.45px;
        border: none;
        transition: 0.3s;
        box-shadow: rgba(0, 0, 0, 0) 0px 0px 0px 0px, rgba(0, 0, 0, 0) 0px 0px 0px 0px, rgba(0, 0, 0, 0.25) 0px 25px 50px -12px;

        &-text {
            position: relative;
            z-index: 2;
            font-weight: 500;
        }

        &::before {
            content: '';
            bottom: -100%;
            height: 200%;
            width: 100%;
            position: absolute;
            left: 0;
            background: linear-gradient(45deg, $libertyPurple -50%, $unitedNationsBlue 150%);
            transition: 0.3s;
            z-index: 1;
        }

        &:hover {
            color: white;
            background: linear-gradient(45deg, $libertyPurple -50%, $unitedNationsBlue 150%);
            border: none;
            outline: none;
            transform: scale(1.1);
            filter: brightness(120%);

            &::before {
                bottom: 0;
            }
        }
    }
}


