        margin: 0 1rem;
        border-radius: .0015px;
        background: linear-gradient(45deg, $libertyPurple 0%, $unitedNationsBlue 100%);

        &::before {
            content: '';
            width: 100%;
            height: 100%;
            left: 0;
            top: 0;
            content: '';
            opacity: 0.6;
            z-index: 0;
            filter: brightness(25%);
            position: absolute;
            background: url('../images/ship.jpeg');
            background-size: cover;
            background-position: bottom left;
            border-radius: .0015px;
        }
    }

    &__heading {
        font-weight: 700;
        letter-spacing: 0.45px;
        color: white;
        position: relative;
        z-index: 2;
    }

    &__subheading {
        font-weight: 500;
        color: white;
        font-size: $fontSizeBodyM;
        position: relative;
        z-index: 2;
        width: 100%;
    }

    &__email {
        border-radius: .008px;
        color: $darkGrey;
        display: inline-block;
        position: relative;
        overflow: hidden;
        z-index: 3;
        padding: 0.5rem 1rem;
        letter-spacing: 0.45px;
        border: none;
        transition: 0.3s;
        background: white;
        min-width: 200px;
        border: 3px solid white;
        box-shadow: rgba(0, 0, 0, 0) 0px 0px 0px 0px, rgba(0, 0, 0, 0) 0px 0px 0px 0px, rgba(0, 0, 0, 0.25) 0px 25px 50px -12px;

        &:focus {
            outline: none;
            border: 3px solid $unitedNationsBlue;
        }
    }

    &__btn {
        border-radius: .008px;
        color: white;
        display: inline-block;
        position: relative;
        overflow: hidden;
        z-index: 3;
        padding: 0.5rem 2rem;
        letter-spacing: 0.45px;
        border: none;
        transition: 0.3s;
        box-shadow: rgba(0, 0, 0, 0) 0px 0px 0px 0px, rgba(0, 0, 0, 0) 0px 0px 0px 0px, rgba(0, 0, 0, 0.25) 0px 25px 50px -12px;

        &-text {
            position: relative;
            z-index: 2;
            font-weight: 500;
        }

        &::before {
            content: '';
            bottom: -50%;
            height: 200%;
            width: 100%;
            position: absolute;
            left: 0;
            background: linear-gradient(45deg, $libertyPurple 0%, $unitedNationsBlue 100%);
            transition: 0.3s;
            z-index: 1;
        }

        &:hover {
            color: white;
            background: linear-gradient(45deg, $libertyPurple 0%, $unitedNationsBlue 100%);
            border: none;
            outline: none;
            transform: scale(1.1);

            &::before {
                bottom: 0;
            }
        }
    }

    &__info {
        position: relative;
        z-index: 2;

        a {
            color: $unitedNationsBlue;
            position: relative;
            z-index: 5;
        }
    }
}

.contact {
    color: white;
    margin-top: $section-margin;


    &__text-container {
        // background: $darkGrey;
        background: white;
        background: linear-gradient(45deg, #111111 -110%, $unitedNationsBlue 110%);
        border-radius: .0015px;
        min-height: 30vh;
        padding: 1.5rem;
        display: flex;
        flex-direction: column;
        justify-content: flex-end;
        position: relative;
        overflow: hidden;
        color: white;
    }

    &__info-container {
        background: white;
        background: linear-gradient(45deg, #111111 -110%, $unitedNationsBlue 110%);
        border-radius: .0015px;
        padding: 1.5rem;
        display: flex;
        flex-direction: column;
        position: relative;
        color: white;
    }

    &__info {
        line-height: 1.8;
        font-weight: 500;
    }

    &__map {
        width: 100%;
        height: 100%;
        min-height: 50vh;
        border-radius: .0015px;
    }
}

.footer {
    display: flex;
    justify-content: center;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
    background: $darkGrey;
    color: white;

    .container {
        border-radius: .0015px;
        margin: 0 1rem;
        position: relative;
        padding: 3rem 1rem;
    }

    &__logo {
        width: 100%;

        svg {
            height: 100px;
            width: 100%;
        }

        svg path,
        svg rect {
            fill: white;
        }
    }

    &__heading {
        font-weight: 700;
        letter-spacing: 0.45px;
        text-transform: uppercase;
        background: linear-gradient(45deg, $unitedNationsBlue 0%, $libertyPurple 100%);
        background: -webkit-linear-gradient(45deg, $unitedNationsBlue 0%, $libertyPurple 100%);
        background-clip: text;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;

    }

    &__ul {
        padding-left: 0;
    }

    &__li {
        list-style: none;
        font-weight: 500;
        line-height: 2;
    }

    &__link {
        color: white;
        transition: 0.3s;

        &:hover {
            color: $unitedNationsBlue;
            text-decoration: none;
            padding-left: 0.5rem;
        }
    }

    &__author-name {
        display: inline-block;
        margin: 0 0.5rem;
        font-weight: 600;
        text-transform: uppercase;
        background: linear-gradient(45deg, $unitedNationsBlue 0%, $libertyPurple 100%);
        background: -webkit-linear-gradient(45deg, $unitedNationsBlue 0%, $libertyPurple 100%);
        background-clip: text;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        padding: 0.25rem 0.5rem;
        border-radius: .000.5rem;
        transition: 0.3s;

        &:hover {
            transform: scale(1.1);
        }
    }
}

@media (max-width:1200px) {
    .intro {
        &__heading {
            font-size: $fontSizeH2;
        }
    }

    .jobs {
        &__subheading {
            font-size: $fontSizeH2;
        }

        &__img {
            min-height: 30vh;
            max-height: 50vh;

            &::before {
                background-position: center 700px;
            }
        }
    }

    .footer {
        text-align: center;
    }

}

@media (max-width:768px) {
    .header {
        bottom: initial;
        top: 1rem;
        left: 1rem;
        width: calc(100% - 2rem);
        padding: 0;
        height: 48px;

        &__logo {
            padding: 0;

            svg path,
            svg rect {
                fill: white;
            }
        }

        &__nav {
            position: fixed;
            top: 0;
            right: 0;
            bottom: 0;
            left: 0;
            background: rgba(0, 0, 0, 0.8);
            transform: translate3d(0, -100%, 0);
            transition-duration: $durationL;
            transition-timing-function: $bezierFastoutSlowin;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            backdrop-filter: blur($spaceXS);
            max-width: 100%;
            width: 100%;
            opacity: 0;


            &--entered {
                transform: translateZ(0);
                opacity: 1;
            }

        }

        &__nav-list {
            transform: none;
            display: block;
            flex-direction: initial;
        }

        &__nav-link {
            width: 100%;
            display: block;
            font-size: $fontSizeBodyL;
            padding: 16px;
            text-align: center;
        }

        .nav-toggle {
            display: flex;

            &--entered {
                .nav-toggle__icon--menu {
                    opacity: 0;
                    transform: translate(-10%, -50%);
                }

                .nav-toggle__icon--close {
                    opacity: 1;
                    transform: translate(-50%, -50%) rotate(0);
                }
            }
        }
    }

    .jobs {
        &__img {
            max-height: 50vh;
        }

        &__job-container {
            row-gap: 0.25rem;
        }
    }

    .offer {
        &__carousel-link {
            height: 384px;
        }

        &__heading-svg {
            width: 13rem;
            height: 30rem;
            position: absolute;
            top: -15rem;
            right: -5rem;
            opacity: 0.8;
        }
    }

    .documents {
        &__heading-svg {
            width: 13rem;
            height: 30rem;
            position: absolute;
            top: -15rem;
            right: -5rem;
            opacity: 0.8;
        }
    }
}

@media (max-width: 480px) {

    .offer {
        &__carousel-link {
            height: 320px;
        }
    }
}
