.container-fluid {
    width: 100%;
    max-width: 1700px;
}

.header {
    position: fixed;
    z-index: 1024;
    width: 100%;
    display: flex;
    top: 1rem;

    .container-fluid {
        // background: linear-gradient(45deg, #BB231B 0%, #2216af 100%);
        border-radius: 10px;
        position: relative;
        overflow: hidden;
        box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);

        &::before {
            content: '';
            display: block;
            position: absolute;
            top: 0;
            left: -500%;
            width: 500%;
            height: 100%;
            background-color: #FEE140;
