        flex-grow: 10;
        display: flex;
        align-items: center;
        justify-content: flex-end;
    }

    &__nav-list {
        display: flex;
    }

    &__nav-link {
        padding: $spaceM;
        color: white;
        font-weight: $fontWeightMedium;
        font-size: initial;
        text-decoration: none;
        position: relative;
        transition: color $durationS ease .1s;
        line-height: 1;
        padding: $spaceS;
        margin: $spaceS;
        border-radius: $spaceXS;
        letter-spacing: $spaceLetters;
        transition: $bezierFastoutSlowin 0.36s;

        &:active,
        &:hover {
            text-decoration: none;
            background-color: white;
            color: black;
        }

        &.active {
            background-color: white;
            color: black;
        }
    }

    &__nav-icons {
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;
        z-index: 16;

        svg path {
            fill: white;
        }
    }

    &__nav-icon-link {
        cursor: pointer;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: $spaceS;
        width: $space2XL;
        height: $space2XL;
        color: white;
        transition: $durationM $bezierFastoutSlowin;

        &:hover {
            svg {
                transform: scale(1.2);
            }
        }

        &:active {
            svg {
                transform: scale(1);
            }
        }

        svg {
            padding: 1px;
            transition: $durationM ease;

            path {
                fill: white;
                transition: fill $durationM ease;
            }
        }
    }

}

